static s7_double multiply_rf_xx(s7_scheme *sc, s7_pointer **p)
{
  s7_rf_t r1, r2;
  s7_double v1;
  r1 = (s7_rf_t)(**p); (*p)++;
  r2 = (s7_rf_t)(**p); (*p)++;
  v1 = r1(sc, p);
  return(v1 * r2(sc, p));
}

