static bool c_function_is_ok(s7_scheme *sc, s7_pointer x)
{
  /* macro version of this (below) is much slower! */
  s7_pointer p;

  p = car(x);
  if (is_global(p)) p = slot_value(global_slot(p)); else p = find_symbol_unchecked(sc, p);
  /* this is nearly always global and p == ecdr(x) */
  return((p == ecdr(x)) ||
	 ((is_any_c_function(p)) && (ecdr(x)) &&
	  (c_function_class(p) == c_function_class(ecdr(x)))));
}

#if 0
#define c_function_is_ok(Sc, X) \
  ({ s7_pointer _X_, _p_;  _X_ = X; _p_ = find_global_symbol_checked(Sc, car(_X_)); \
   ((_p_ == ecdr(_X_)) || ((is_any_c_function(_p_)) && (c_function_class(_p_) == c_function_class(ecdr(_X_))))); })
#endif

static bool arglist_has_rest(s7_scheme *sc, s7_pointer args)
{
  s7_pointer p;
  for (p = args; is_pair(p); p = cdr(p))
    if (car(p) == sc->KEY_REST)
      return(true);
  return(false);
}


static bool arglist_has_keyword(s7_pointer args)
{
  s7_pointer p;
  for (p = args; is_pair(p); p = cdr(p))
    if (is_keyword(car(p)))
      return(true);
  return(false);
}


static s7_function end_dox_eval(s7_scheme *sc, s7_pointer code);

static void initialize_dox_end_vars(s7_scheme *sc, s7_pointer end)
{
  /* fprintf(stderr, "init: %s %s\n", DISPLAY(end), opt_names[optimize_op(end)]); */

  switch (optimize_op(end))
    {
    case HOP_SAFE_C_S:
    case HOP_SAFE_C_SC:
    case HOP_SAFE_C_S_opCq:
      dox_slot1(sc->envir) = find_symbol(sc, cadr(end));
      dox_slot2(sc->envir) = NULL;
      break;

    case HOP_SAFE_C_CS:
    case HOP_SAFE_C_QS:
      dox_slot1(sc->envir) = find_symbol(sc, caddr(end));
      dox_slot2(sc->envir) = NULL;
      break;

    case HOP_SAFE_C_SS:
      dox_slot1(sc->envir) = find_symbol(sc, cadr(end));
      dox_slot2(sc->envir) = find_symbol(sc, caddr(end));
      break;

    case HOP_SAFE_C_opSq_S:
      dox_slot1(sc->envir) = find_symbol(sc, cadr(cadr(end)));
      dox_slot2(sc->envir) = find_symbol(sc, caddr(end));
      break;

    case HOP_SAFE_C_S_opSq:
    case HOP_SAFE_C_S_opSCq:
    case HOP_SAFE_C_S_opSSq:
      dox_slot1(sc->envir) = find_symbol(sc, cadr(end));
      dox_slot2(sc->envir) = find_symbol(sc, ecdr(cdr(end)));
      break;

    case HOP_SAFE_C_opSq_opSq:
      dox_slot1(sc->envir) = find_symbol(sc, cadr(cadr(end)));
      dox_slot2(sc->envir) = find_symbol(sc, cadr(caddr(end)));
      break;
      
    default:
      dox_slot1(sc->envir) = NULL;
      dox_slot2(sc->envir) = NULL;
      break;
    }
}



